/*
This file connects to the remote prisma database currently
hosted on prisma.io and gives us the ability to query it with JS
 */

const { Prisma } = require('prisma-binding')

const db = new Prisma({
    // the graphql schema file generated by the prisma cli on deploy
    // the path is relative to the root of the app
    typeDefs: 'src/generated/prisma.graphql',
    // environment variable from variables.env file
    endpoint: process.env.PRISMA_ENDPOINT,
    // environment variable from variables.env file
    secret: process.env.PRISMA_SECRET,
    debug: false
})

module.exports = db